/*
 * System.out.printf() => JDK 1.5 => C언어에서 도입
 * 	서식이 있는 출력
 * 	%d : 정수값 / %f : 실수값 / %c: 문자값 / %s:문자열값
 */

public class 문제1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		   int a;   
		   int b;   
		   a=3;   
		   b=a+5;       
		   System.out.printf("a의 값: %d \n", a);   // a의 값: 3
		   System.out.printf("b의 값: %d \n", b);   // b의 값: 8
		
	}
	
}